mapper.insert:
insert into member

	(userId, userPwd, userName, gender, mobile, email, hobby, userImg)
	values(#{userId}, sha1(md5(#{userPwd})), #{userName}, #{gender}, #{mobile}, #{email}, #{hobby}, #{userImg});


